mapper.select:
select pp.id,pp.money,pp.type,pp.org_id,pp.org_name,date_format(pp.payment_time,'%Y-%m-%d') as paymentTimeStr,pp.remarks, pp.party_org_id,pp.title,if(pp.type=0,pp.money,'') as inMoney,if(pp.type=1,pp.money,'') as outMoney,pp.balance from dj_party_payments pp where pp.deleted = 0
and pp.org_id = #{t.orgId}
and pp.title like concat('%',#{t.title},'%')
and pp.type =#{t.type}
and pp.payment_time like concat('%',#{t.year},'%')
and date_format(pp.payment_time,'%Y-%m-%d') >= date_format(#{t.startTime},'%Y-%m-%d')
and date_format(#{t.endTime},'%Y-%m-%d') >= date_format(pp.payment_time,'%Y-%m-%d')
/*order by pp.payment_time desc, pp.update_time desc*/ order by pp.create_time desc

select ifnull(sum(pp.money),0)
from dj_party_payments pp where pp.deleted = 0


        and YEAR(pp.payment_time)=YEAR(#{t.year})


        and YEAR(pp.payment_time)=YEAR(now())



    and pp.org_id = #{t.orgId}


    and pp.party_org_id = #{t.partyOrgId}


    and pp.type = #{t.type}

select ifnull(sum(pp.money),0)
from dj_party_payments pp where pp.deleted = 0


        and YEAR(pp.payment_time)=YEAR(#{t.year})


        and YEAR(pp.payment_time)=YEAR(now())


and pp.org_id in

    #{id}


    and pp.party_org_id = #{t.partyOrgId}


    and pp.type = #{t.type}

SELECT concat(a.month,'月') as month , ifnull(b.money, 0) partyMoney FROM (SELECT 1 month UNION ALL SELECT 2 month UNION ALL SELECT 3 month UNION ALL SELECT 4 month UNION ALL SELECT 5 month UNION ALL SELECT 6 month UNION ALL SELECT 7 month UNION ALL SELECT 8 month UNION ALL SELECT 9 month UNION ALL SELECT 10 month UNION ALL SELECT 11 month UNION ALL SELECT 12 month ) a left join (select MONTH(pp.payment_time) month,sum(pp.money) as money from dj_party_payments pp where pp.deleted = 0 and YEAR(payment_time)=YEAR(now())
and pp.org_id = #{t.orgId}
and pp.party_org_id = #{t.partyOrgId}
and pp.type = #{t.type}
GROUP BY MONTH(pp.payment_time)) b on a.month = b.month ORDER BY a.month

select pp.id,pp.money,pp.type,pp.org_id,pp.org_name,date_format(pp.payment_time,'%Y-%m-%d') as paymentTimeStr,pp.remarks,
pp.party_org_id,pp.title,if(pp.type=0,pp.money,'') as inMoney,if(pp.type=1,pp.money,'') as outMoney,pp.balance
from dj_party_payments pp where pp.deleted = 0

    and pp.org_id = #{t.orgId}


    and pp.title like concat('%',#{t.title},'%')


    and pp.type =#{t.type}



        and YEAR(pp.payment_time)=YEAR(#{t.year})


        and YEAR(pp.payment_time)=YEAR(now())


/*order by pp.payment_time desc, pp.update_time desc*/
order by pp.create_time desc
