mapper.select:
select s.id, s.apply_id, s.process_id, s.proc_inst_id, s.task_id, s.task_node_code, s.create_user_id, s.task_assignee_user_id, s.task_assignee_real_name, s.task_status, s.form_key, s.claim_time, s.create_time, s.create_by, s.form_edit_field, a.apply_title, a.apply_status, a.apply_company_name, a.model_id, a.process_name, a.apply_dept_name, a.create_time as apply_date, m.form_id, m.form_type, m.form_code from bpm_send_duplicate s, bpm_apply a, bpm_model m where a.id = s.apply_id and a.model_id = m.id and s.deleted = 0 and a.deleted = 0
and s.task_assignee_user_id = #{sendDuplicate.taskAssigneeUserId}
and s.create_user_id = #{sendDuplicate.createUserId}
and s.apply_id = #{sendDuplicate.applyId}
and a.apply_title like concat('%', #{sendDuplicate.applyTitle}, '%')
and ( a.id = #{sendDuplicate.appParam} or a.apply_title like CONCAT('%',#{sendDuplicate.appParam},'%') or a.process_name like CONCAT('%',#{sendDuplicate.appParam},'%') or a.apply_real_name like CONCAT('%',#{sendDuplicate.appParam},'%') )
order by s.create_time desc
