mapper.select:
select m.* from sys_menu m
INNER JOIN sys_permission p on p.permission_id = m.id and p.type = 0
where m.deleted = 0
and m.type = #{type}
and m.enabled= #{enabled}
and p.role_id in

    #{roleId}

select id,name as label,pid,icon,type from sys_menu where deleted = 0 and enabled = #{enabled}

    and type = #{type}

order by code

select m.* from sys_menu m
where m.deleted = 0
and m.type = #{type}
and m.enabled= #{enabled}
